import React, { Component } from "react";
import axios from "axios";
import "./App.css";
import Search from "./components/Search";

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: ""
    };
  }

  handleChange = (e) => {
    this.setState({
      user: e.target.value
    });
  };

  handleAdd = (name) =>{
    this.setState({
      user : name
    })
  }

  render() {
    return (
      <div>
        <Search changed={this.handleChange} />
        {console.log(this.state.user)}
      </div>
    );
  }
}
