import React, { Component } from 'react'
import axios from "axios";
export default class Table extends Component {
    constructor(props) {
        super(props);
        this.state = {
            profiles : []
        }
    }

    componentDidMount = () => {
        axios.get('https://api.github.com/search/users', {
            params: {
              q: this.props.name
            }
          }).then(response => {
            this.setState({profiles : response.data.items})
          })
          .catch(function (error) {
            console.log(error);
          });
    }
    componentDidUpdate = () => {
        axios.get('https://api.github.com/search/users', {
            params: {
              q: this.props.name
            }
          }).then(response => {
            this.setState({profiles : response.data.items})
          })
          .catch(function (error) {
            console.log(errorMessage, error);
          });
    }

    render() {
        // const style = {
        //     width : 
        // }
        return (
            <div>
                {/* {console.log(this.state.profiles[0].obj)} */}
                <table className="table">
                    <thead className="thead-dark">
                        <tr>
                            <th scope="col">ID</th>
                            <th scope="col">Login Name</th>
                            <th scope="col">Profile Pic</th>
                            <th scope="col">Type</th>
                            <th scope="col">Github Profile</th>
                            <th scope="col">Score</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.state.profiles.map((e) => {
                            return( 
                                <tr key = {e.id}>
                                    <td>{e.id} </td>
                                    <td>{e.login} </td>
                                    <td className = "imgSize"><img src={e.avatar_url} /></td>
                                    <td> {e.login} </td>
                                    <td>{e.html_url} </td>
                                    <td>{e.score} </td>
                                </tr>
                            )
                        })}
                    </tbody>
                </table>
            </div>
        )
    }
}
